import { memo, useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import './PartnerHomeHadService.scss'
import { AiOutlineSearch, AiOutlinePlus, AiFillCaretLeft, AiFillCaretRight, AiOutlineLeft, AiOutlineRight, AiOutlineEdit, AiOutlineDelete } from 'react-icons/ai'
import { BsThreeDotsVertical } from 'react-icons/bs'
import { GoDashboard } from 'react-icons/go'
import { GrServices } from 'react-icons/gr'
import Select from 'react-select';
import { Menu, MenuItem, MenuButton } from '@szhsin/react-menu';
import '@szhsin/react-menu/dist/index.css';
import '@szhsin/react-menu/dist/transitions/slide.css';
import { Sidebar, Menu as MenuSide, MenuItem as MenuItemSide, useProSidebar } from 'react-pro-sidebar';

function PartnerHomeHadService() {
    useEffect(() => {
        window.scrollTo(0, 0)
    })

    const [searchName, setSearchName] = useState('')
    const [searchStatus, setSearchStatus] = useState(0)
    const [searchAccomodation, setSearchAccomodation] = useState(-1)
    const [numberPage, setNumberPage] = useState(1)

    const { collapseSidebar } = useProSidebar();
    const [isOpenSidebar, setOpenSidebar] = useState(true)

    const handleClickOpenCloseSidebar = () => {
        setOpenSidebar(!isOpenSidebar);
        collapseSidebar();
    }

    const options = [
        { value: 0, label: 'All' },
        { value: 1, label: 'Active' },
        { value: 2, label: 'Close' },
        { value: 3, label: 'Pause' }
    ]

    const optionsAccomodation = [
        { value: -1, label: 'All' },
        { value: 0, label: 'Hotel' },
        { value: 1, label: 'Homestay' },
        { value: 2, label: 'Motel' }
    ]

    const listService = [
        {
            name: 'Sóng đỏ',
            accommodation: 0,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng vàng',
            accommodation: 1,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 3
        },
        {
            name: 'Sóng đen',
            accommodation: 1,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng đmm',
            accommodation: 0,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng vcc',
            accommodation: 2,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 2
        },
        {
            name: 'Sóng qq',
            accommodation: 1,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng gió',
            accommodation: 0,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 3
        },
        {
            name: 'Cô Linh lăng nhăng',
            accommodation: 2,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 3
        },
        {
            name: 'Quán cơm',
            accommodation: 2,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 2
        },
        {
            name: 'Vcc',
            accommodation: 1,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Vcc',
            accommodation: 1,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Vcc',
            accommodation: 0,
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        }
    ]

    const listServiceShow = []

    for (let i = 0; i < listService.length; i++) {
        if ((searchName === '' ||
            (listService[i].name.toLowerCase().includes(searchName.toLowerCase()) &&
                listService[i].name.toUpperCase().includes(searchName.toUpperCase()))) &&
            (searchStatus === 0 || listService[i].status === searchStatus)) {
            console.log(listService[i].name)
            listServiceShow.push(listService[i])
        }
    }

    let numberOfPage = []

    for (let i = 0; i < listServiceShow.length / 10; i++) {
        numberOfPage.push(i + 1)
    }

    return (
        <div className='home-partner-main d-flex'>
            <Sidebar>
                <MenuSide>
                    <MenuItemSide onClick={handleClickOpenCloseSidebar} icon={isOpenSidebar ? <AiFillCaretLeft /> : <AiFillCaretRight />} />
                    <MenuItemSide icon={<GoDashboard />}> Dashboard </MenuItemSide>
                    <MenuItemSide icon={<GrServices />}> Services </MenuItemSide>
                </MenuSide>
            </Sidebar>

            <div className='container list-service-main' id='list-service-main'>
                <div className='d-flex tool-list-service-main'>
                    <div className='d-flex list-service-tool'>
                        <label htmlFor='search-name' className='search-input-text'>
                            <AiOutlineSearch className='icon-inner icon-search-list-service' />
                            <input placeholder='Name' onChange={(e) => setSearchName(e.target.value)} id='search-name' type='text' className='input-inline input-list-service' />
                        </label>
                        <Select onChange={(e) => setSearchStatus(e.value)} placeholder='Status' isSearchable={false} className='input-list-service select-search-service' options={options} />
                        <Select onChange={(e) => setSearchAccomodation(e.value)} placeholder='Accommodation' isSearchable={false} className='input-list-service select-search-service' options={optionsAccomodation} />
                    </div>
                    <Link to='/partner/select-detail-service' className='btn btn-primary btn-add-new-service'><AiOutlinePlus /> Add new</Link>
                </div>
                <div className='space-table'>
                    <table className='table table-hover table-list-service mt-30'>
                        <thead>
                            <tr>
                                <td>STT</td>
                                <td>Name</td>
                                <td>Accommodation</td>
                                <td>Address</td>
                                <td>Status</td>
                                <td>Action</td>
                            </tr>
                        </thead>
                        <tbody>
                            {listServiceShow.map((service, index) => (index + 1 <= numberPage * 10 && index + 1 >= (numberPage * 10 - 9) &&
                                <tr>
                                    <td>{index + 1}</td>
                                    <td>{service.name}</td>
                                    <td>
                                        {(service.accommodation === 0 && 'Hotel') || 
                                        (service.accommodation === 1 && 'Homestay') || 
                                        (service.accommodation === 2 && 'Motel')}
                                    </td>
                                    <td>{service.address}</td>
                                    <td>
                                        {(service.status === 1 && <label className='status status-active'>Active</label>) ||
                                            (service.status === 2 && <label className='status status-close'>Close</label>) ||
                                            (service.status === 3 && <label className='status status-pause'>Pause</label>)}
                                    </td>
                                    <td>
                                        <Menu menuButton={<MenuButton className='btn-action'><BsThreeDotsVertical /></MenuButton>} transition>
                                            <MenuItem><AiOutlineEdit />Edit</MenuItem>
                                            <MenuItem><AiOutlineDelete />Delete</MenuItem>
                                        </Menu>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <div className='d-flex float-end paging'>
                    {numberPage > 1 && <label onClick={() => setNumberPage(pre => pre - 1)} className='btn-paging unseleted'>
                        <AiOutlineLeft />
                    </label>}
                    {numberOfPage.map((item) => (
                        <label className={`btn-paging ${numberPage === item ? 'selected-paging' : 'unseleted'}`} onClick={() => setNumberPage(item)}>{item}</label>
                    ))}
                    {numberPage === 1 && numberOfPage.length > 1 && <label onClick={() => setNumberPage(pre => pre + 1)} className='btn-paging unseleted'>
                        <AiOutlineRight />
                    </label>}
                </div>
            </div>
        </div>
    )
}

export default memo(PartnerHomeHadService)
