import usePlacesAutocomplete, { getGeocode, getLatLng } from 'use-places-autocomplete'
import { Combobox, ComboboxInput, ComboboxPopover, ComboboxList, ComboboxOption } from '@reach/combobox'
import '@reach/combobox/styles.css'
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';
import { memo, useState, useCallback } from 'react'
import './GoogleMapLayout.scss'

function GoogleMapLayout({ style, tour, setTour, hadOnclick }) {
    const center = {
        lat: tour.latitude,
        lng: tour.longitude
    };

    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyBZDeLRK0328QFPaSs-OCk03bMUOHXaMys",
        libraries: ["places"]
    })

    const handleClickMap = ((latLng) => {
        setTour({ ...tour, latitude: latLng.lat(), longitude: latLng.lng() })
    })

    const [map, setMap] = useState(null)

    const onLoad = useCallback(function callback(map) {
        const bounds = new window.google.maps.LatLngBounds(center);
        map.fitBounds(bounds);
        setMap(map)
    }, [])

    const onUnmount = useCallback(function callback(map) {
        setMap(null)
    }, [])

    console.log(tour.latitude, tour.longitude)

    return (isLoaded &&
        <div>

            <GoogleMap
                mapContainerStyle={style}
                center={center}
                zoom={15}
                onLoad={onLoad}
                onUnmount={onUnmount}
                onClick={hadOnclick && ((e) => handleClickMap(e.latLng))}
            >
                <PlacesAutoComplete setTour={setTour} tour={tour} />
                <Marker position={{
                    lat: tour.latitude,
                    lng: tour.longitude
                }} />
            </GoogleMap>
        </div>
    );
}

const PlacesAutoComplete = ({ setTour, tour }) => {
    const {
        ready,
        value,
        setValue,
        suggestions: { status, data },
        clearSuggestions
    } = usePlacesAutocomplete()

    const handleSelect = (async (address) => {
        setValue(address, false)
        clearSuggestions()

        const results = await getGeocode({ address });
        const { lat, lng } = await getLatLng(results[0])
        setTour({ ...tour, latitude: lat })
        setTour({ ...tour, longitude: lng })
    })

    return <Combobox onSelect={handleSelect} className='z-index-combobox-maps'>
        <ComboboxInput value={value} onChange={e => setValue(e.target.value)} disabled={!ready} className='combobox-input-maps' />
        <ComboboxPopover>
            <ComboboxList>
                {status === 'OK' && data.map(({ place_id, description }) => (
                    <ComboboxOption key={place_id} value={description} />
                ))
                }
            </ComboboxList>
        </ComboboxPopover>
    </Combobox>
}

export default memo(GoogleMapLayout)
