import { memo, useContext, useState } from 'react'
import { OnShowSidebar } from '../../Layout/AdminHome'
import { english, vietnamese } from '../../Languages/OptionInputInformationService'
import './ViewInformationRooms.scss'

function ViewInformationRooms({ languageSelected }) {
    const isShowSidebar = useContext(OnShowSidebar)
    let menuLanguage = (languageSelected === 'EN' ? english : vietnamese)
    const categoryService = sessionStorage.getItem('index-service-selected') - 1

    const [optionMenuService, setOptionMenuService] = useState(0)

    return (
        <div>
            <div className={`menu-view-service-fixed ${isShowSidebar && 'menu-hidden'}`}>
                {menuLanguage[categoryService].map((option, index) => (
                    <label onClick={() => setOptionMenuService(index)}
                        className={`d-block option-menu-fixed ${optionMenuService === index && 'selected-menu-service'}`}>{option}</label>
                ))}
            </div>
            <div>

            </div>
        </div>
    )
}

export default memo(ViewInformationRooms)
