import { memo } from 'react'
import { useLocation } from 'react-router-dom'
import './PostDetailForum.scss'
import { english, typePostEnglish, typePostVietnamese, vietnamese } from '../../Languages/ListPostForum'

function PostDetailForum({ languageSelected }) {
    const post = useLocation().state.post

    const languageTypePost = languageSelected === 'EN' ? typePostEnglish : typePostVietnamese

    return (
        <div className='container'>
            <img src={post.image} className='bg-image rotation-0 mt-20 mb-20' />
            <div className='topic-post mb-20'>{languageTypePost[parseInt(post.topic) - 1].label.toUpperCase()}</div>
        </div>
    )
}

export default memo(PostDetailForum)
