import { memo } from 'react'
import { Rating } from 'react-simple-star-rating'
import './RegisterInformationAccomodation.scss'

function RegisterInformationAccomodation({ languageSelected }) {
    const handleBlur = (input) => {
        input.style.border = 'solid 1px #D9D9D9'
    }

    const handleFocus = (input) => {
        input.style.border = 'solid 1px #4874E8'
    }

    return (
        <div className='container'>
            <div className='space-30 background-w'>
                <div className="line-input">
                    <div className="input-alone">
                        <label htmlFor='roomName' className='d-block'>Property Name<span className="requird-star">*</span></label>
                        <input id='roomName' className='input-inline'
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)} />
                    </div>
                </div>
                <div className="d-flex line-input line-input-3-line">
                    <div className='w-45'>
                        <label htmlFor="numberOfRoom" className="d-block">Number of rooms<span className="requird-star">*</span></label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            id='numberOfRoom' className="input-inline" type='number'/>
                    </div>
                    <div className='w-45'>
                        <label htmlFor="bedType" className="d-block">Star rating<span className="requird-star">*</span></label>
                        
                    </div>
                </div>
            </div>
        </div>
    )
}

export default memo(RegisterInformationAccomodation)
