import { memo, useState, createContext } from 'react'
import { english, vietnamese } from '../../Languages/OptionInputInformationService'
import ViewInformationAccommodation from '../ViewInformationAccommodation'
import ViewInformationEntertainment from '../ViewInformationEntertainment'
import ViewInformationRestaurant from '../ViewInformationRestaurant'
import ViewInfomationRooms from '../ViewInfomationRooms'
import ViewUtilitiesAndServices from '../ViewUtilitiesAndServices'
import ViewPhotos from '../ViewPhotos'

import './ViewInformationServicePending.scss'

export const CommonDataForAllViewService = createContext()
// sessionStorage.setItem('index-service-selected', 1)

function ViewInformationServicePending({ languageSelected }) {
    let titleLanguage = (languageSelected === 'EN' ? english : vietnamese)
    const categoryService = sessionStorage.getItem('index-service-selected') - 1

    //form input information
    const [propertyName, setPropertyName] = useState('')
    const [status, setStatus] = useState(1)
    const [starRating, setStarRating] = useState(1)
    const [city, setCity] = useState('Hà Nội')
    const [address, setAddress] = useState('')
    const [website, setWebsite] = useState('')
    const [taxCode, setTaxCode] = useState('')
    const [description, setDescription] = useState('')
    const [fax, setFax] = useState('')
    const [phoneNumberContact, setPhoneNumberContact] = useState('')
    const [emailContact, setEmailContact] = useState('')

    //feild for accomodation
    const [numberOfFloors, setNumberOfFloors] = useState(0)

    //feild for restaurant and entertainment
    const [timeOpen, setTimeOpen] = useState('')
    const [timeClose, setTimeClose] = useState('')

    //feild for restaurant
    const [typeOfCuisine, setTypeOfCuisine] = useState([])

    //feild for entertainment
    const [dateOpen, setDateOpen] = useState([])
    const [childTiketPrice, setChildTiketPrice] = useState(0)
    const [adultTicketPrice, setAdultTicketPrice] = useState(0)

    //form input rooms
    const [roomsData, setroomsData] = useState([
        {
            showHide: false,
            roomName: '',
            roomType: 0,
            numberOfRoom: 0,
            bedType: 0,
            numberOfPeopleRoom: 0,
            roomSize: 0,
            dailyPrice: 0,
            holidayPrice: 0,
            description: '',
            bedRoomPhoto: [],
            wcPhoto: [],
            otherPhoto: []
        }
    ])

    //Utilities and services
    const [utilitiesInternet, setUtilitiesInternet] = useState([])
    const [utilitiesFacilities, setUtilitiesFacilities] = useState([])
    const [utilitiesAccomodationService, setUtilitiesAccomodationService] = useState([])
    const [utilitiesBeautyRelaxation, setUtilitiesBeautyRelaxation] = useState([])
    const [utilitiesFoodService, setUtilitiesFoodService] = useState([])
    const [utilitiesEntertainment, setUtilitiesEntertainment] = useState([])
    const [utilitiesChildrenService, setUtilitiesChildrenService] = useState([])
    const [utilitiesShopping, setUtilitiesShopping] = useState([])
    const [utilitiesBusinessService, setUtilitiesBusinessService] = useState([])
    const [utilitiesVehicle, setUtilitiesVehicle] = useState([])

    //Photos
    const [receptionHallPhoto, setReceptionHallPhoto] = useState([])
    const [outdoorPhoto, setOutdoorPhoto] = useState([])
    const [otherPhoto, setOtherPhoto] = useState([])

    let serviceDTO = {
        propertyName: propertyName,
        status: status,
        starRating: starRating,
        city: city,
        address: address,
        website: website,
        taxCode: taxCode,
        description: description,
        fax: fax,
        phoneNumberContact: phoneNumberContact,
        emailContact: emailContact,
        utilitiesInternet: utilitiesInternet,
        utilitiesFacilities: utilitiesFacilities,
        utilitiesAccomodationService: utilitiesAccomodationService,
        utilitiesBeautyRelaxation: utilitiesBeautyRelaxation,
        utilitiesFoodService: utilitiesFoodService,
        utilitiesEntertainment: utilitiesEntertainment,
        utilitiesChildrenService: utilitiesChildrenService,
        utilitiesShopping: utilitiesShopping,
        utilitiesBusinessService: utilitiesBusinessService,
        utilitiesVehicle: utilitiesVehicle,
        receptionHallPhoto: receptionHallPhoto,
        outdoorPhoto: outdoorPhoto,
        otherPhoto: otherPhoto
    }

    const listScreen = [
        [
            <ViewInformationAccommodation languageSelected={languageSelected} roomsData={roomsData} numberOfFloors={numberOfFloors} />,
            <ViewInfomationRooms languageSelected={languageSelected} formFields={roomsData} setFormFields={setroomsData} />,
            <ViewUtilitiesAndServices languageSelected={languageSelected} />,
            <ViewPhotos languageSelected={languageSelected} />
        ],
        [
            <ViewInformationEntertainment languageSelected={languageSelected} timeOpen={timeOpen} timeClose={timeClose} dateOpen={dateOpen}
                childTiketPrice={childTiketPrice} adultTicketPrice={adultTicketPrice} />,
            <ViewUtilitiesAndServices languageSelected={languageSelected} />,
            <ViewPhotos languageSelected={languageSelected} />
        ],
        [
            <ViewInformationRestaurant languageSelected={languageSelected} timeOpen={timeOpen} timeClose={timeClose} typeOfCuisine={typeOfCuisine} />,
            <ViewUtilitiesAndServices languageSelected={languageSelected} />,
            <ViewPhotos languageSelected={languageSelected} />
        ]
    ]

    const [optionSelected, setOptionSelected] = useState(0)

    return (
        <CommonDataForAllViewService.Provider value={serviceDTO}>

            <div className='main-content-view-service-admin'>
                <nav className='d-flex nav-view-service-admin'>
                    {titleLanguage[categoryService].map((item, index) => (
                        <div onClick={() => setOptionSelected(index)}
                            className={`item-nav-view-service-admin 
                        ${optionSelected == index ? 'item-nav-view-service-admin-selected' : 'item-nav-view-service-admin-unselected'}
                        ${index === 0 && 'br-left-top'} ${index === titleLanguage[categoryService].length - 1 && 'br-right-top'}`}>
                            {item}
                        </div>
                    ))}
                </nav>
                {window.location.pathname.includes('confirm') &&
                    <div className='d-flex content-title-view-service'>
                        <label className='title-admin-view-service'>{titleLanguage[categoryService][optionSelected]}</label>
                        <div className='btn-confirm-or-refuse'>
                            <button className='btn btn-success btn-confirm-service'>Confirm</button>
                            <button className='btn btn-danger'>Refuse</button>
                        </div>
                    </div>
                }
                <div className='main-content-right-view-service-under'>
                    {listScreen[categoryService][optionSelected]}
                </div>
            </div>
        </CommonDataForAllViewService.Provider>
    )
}

export default memo(ViewInformationServicePending)
