import { storage } from '../Upload'
import { ref, uploadBytes, listAll, getDownloadURL, getStream } from 'firebase/storage'
import { toast } from 'react-toastify'

export const UploadImage = async (imageList, path, count, leng, id, backPath, needIndex, lastPath) => {
    const image = [...imageList]
    for (let i = 0; i < imageList.length; i++) {
        const imageService = ref(storage, `${path}/${id}/${backPath}${needIndex && `/${i + 1}/${lastPath}`}/${image[i].name}`);
        uploadBytes(imageService, image[i]).then(() => {
            console.log(++leng);
            if (leng === count) {
                toast.success('Xong')
            }
        })
    }
}
