import { memo, useState, useEffect } from 'react'
import { Sidebar, Menu, MenuItem, SubMenu, useProSidebar } from 'react-pro-sidebar';
import { englishAdminMenu, vietnameseAdminMenu } from '../../Languages/AdminMenu';
import { AiFillCaretLeft, AiFillCaretRight } from 'react-icons/ai'
import Dashboard from '../../images/dashboard.png'
import Suppliers from '../../images/supplier.png'
import HeaderOnly from '../HeaderOnly';
import './AdminHome.scss'

function AdminHome({ languageSelected, children }) {
    let adminMenuLabel = (languageSelected === 'EN' ? englishAdminMenu : vietnameseAdminMenu)
    const { collapseSidebar } = useProSidebar();
    const [isOpenSidebar, setOpenSidebar] = useState(true)

    const handleClickOpenCloseSidebar = () => {
        setOpenSidebar(!isOpenSidebar);
        collapseSidebar();
    }

    return (
        <HeaderOnly>
            <div className='container home-main'>
                <div className='home-partner-main d-flex'>
                    <Sidebar className='menu-sidebar-admin'>
                        <Menu>
                            <MenuItem className='menu-item-left' onClick={handleClickOpenCloseSidebar} icon={isOpenSidebar ? <AiFillCaretLeft /> : <AiFillCaretRight />}></MenuItem>
                            <MenuItem icon={<img className='image-icon-sidebar' src={Dashboard} />} className='menu-item-left'>{adminMenuLabel.txtDashboard}</MenuItem>
                            <SubMenu icon={<img className='image-icon-sidebar' src={Suppliers} />} className='menu-item-left menu-sub-main' label={adminMenuLabel.txtSuppliers}>
                                <MenuItem className='menu-item-left'>{adminMenuLabel.txtAccommodation}</MenuItem>
                                <MenuItem className='menu-item-left'>{adminMenuLabel.txtEntertainment}</MenuItem>
                                <MenuItem className='menu-item-left'>{adminMenuLabel.txtAccommodation}</MenuItem>
                            </SubMenu>
                            <SubMenu className='menu-item-left menu-sub-main' label={adminMenuLabel.txtService}>
                                <MenuItem className='menu-item-left'>{adminMenuLabel.txtTourProduct}</MenuItem>
                            </SubMenu>
                            <MenuItem className='menu-item-left'>{adminMenuLabel.txtBooking}</MenuItem>
                            <MenuItem className='menu-item-left'>{adminMenuLabel.txtCustomer}</MenuItem>
                            <MenuItem className='menu-item-left'>{adminMenuLabel.txtPartner}</MenuItem>
                            <MenuItem className='menu-item-left'>{adminMenuLabel.txtFeedback}</MenuItem>
                            <MenuItem className='menu-item-left'>{adminMenuLabel.txtServiceConfirm}</MenuItem>
                        </Menu>
                    </Sidebar>
                    <div className='container list-service-main'>setAddress</div>
                </div>
            </div>
        </HeaderOnly>
    )
}

export default memo(AdminHome)
