import { storage } from '../Upload'
import { ref, uploadBytes, listAll, getDownloadURL, getStream } from 'firebase/storage'
import { toast } from 'react-toastify'

export const UploadImage = async (imageList, path, count, leng, id, backPath, index, lastPath) => {
    const image = [...imageList]
    const pathMain = (index > 0 ? `${path}/${id}/${backPath}/${index}/${lastPath}/${image[i].name}` : `${path}/${id}/${backPath}/${lastPath}/${image[i].name}`)
    for (let i = 0; i < imageList.length; i++) {
        const imageService = ref(storage, pathMain);
        uploadBytes(imageService, image[i]).then(() => {
            console.log(++leng);
            if (leng === count) {
                toast.success('Xong')
            }
        })
    }
}
