import { memo, useState } from 'react'
import { Link } from 'react-router-dom'
import './PartnerHomeHadService.scss'
import { AiOutlineSearch, AiOutlinePlus, AiFillCaretLeft, AiFillCaretRight } from 'react-icons/ai'
import { BsThreeDotsVertical } from 'react-icons/bs'
import { GoDashboard } from 'react-icons/go'
import { GrServices } from 'react-icons/gr'
import Select from 'react-select';
import { Menu, MenuItem, MenuButton } from '@szhsin/react-menu';
import '@szhsin/react-menu/dist/index.css';
import '@szhsin/react-menu/dist/transitions/slide.css';
import { Sidebar, Menu as MenuSide, MenuItem as MenuItemSide, useProSidebar } from 'react-pro-sidebar';

function PartnerHomeHadService() {
    const [status, setStatus] = useState(0)
    const [searchName, setSearchName] = useState('')
    const [searchStatus, setSearchStatus] = useState(0);
    const [numberPage, setNumberPage] = useState(1);

    const { collapseSidebar } = useProSidebar();
    const [isOpenSidebar, setOpenSidebar] = useState(true)

    const handleClickOpenCloseSidebar = () => {
        setOpenSidebar(!isOpenSidebar);
        collapseSidebar();
    }

    const options = [
        { value: 0, label: 'All' },
        { value: 1, label: 'Active' },
        { value: 2, label: 'Close' },
        { value: 3, label: 'Pause' }
    ]

    const listService = [
        {
            name: 'Sóng đỏ',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng vàng',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 3
        },
        {
            name: 'Sóng đen',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng đmm',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng vcc',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 2
        },
        {
            name: 'Sóng qq',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng gió',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 3
        },
        {
            name: 'Cô Linh lăng nhăng',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 3
        },
        {
            name: 'Quán cơm',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 2
        },
        {
            name: 'Vcc',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Vcc',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Vcc',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        }
    ]

    let numberOfPage = []

    for (let i = 0; i < listService.length / 10; i++) {
        numberOfPage.push(i + 1)
    }

    return (
        <div className='home-partner-main d-flex'>
            <Sidebar>
                <MenuSide>
                    <MenuItemSide onClick={handleClickOpenCloseSidebar} icon={isOpenSidebar ? <AiFillCaretLeft /> : <AiFillCaretRight />} />
                    <MenuItemSide icon={<GoDashboard />}> Dashboard </MenuItemSide>
                    <MenuItemSide icon={<GrServices />}> Services </MenuItemSide>
                </MenuSide>
            </Sidebar>

            <div className='container list-service-main'>
                <div className='d-flex tool-list-service-main'>
                    <div className='d-flex list-service-tool'>
                        <label htmlFor='search-name' className='search-input-text'>
                            <AiOutlineSearch className='icon-inner icon-search-list-service' />
                            <input onChange={(e) => setSearchName(e.target.value)} id='search-name' type='text' className='input-inline input-list-service' />
                        </label>
                        <Select onChange={(e) => setSearchStatus(e.value)} placeholder='Status' isSearchable={false} className='input-list-service select-search-service' options={options} />
                        <Select placeholder='???' isSearchable={false} className='input-list-service select-search-service' options={options} />
                    </div>
                    <Link to='/partner/select-detail-service' className='btn btn-primary btn-add-new-service'><AiOutlinePlus /> Add new</Link>
                </div>
                <div className='space-table'>
                    <table className='table table-hover table-list-service mt-30'>
                        <thead>
                            <tr>
                                <td>STT</td>
                                <td>Name</td>
                                <td>Address</td>
                                <td>Status</td>
                                <td>Action</td>
                            </tr>
                        </thead>
                        <tbody>
                            {listService.map((service, index) => (index + 1 <= numberPage * 10 && service.name.includes(searchName) &&
                                (searchStatus === 0 || service.status === searchStatus) &&
                                <tr>
                                    <td>{index + 1}</td>
                                    <td>{service.name}</td>
                                    <td>{service.address}</td>
                                    <td>
                                        {(service.status === 1 && <label className='status status-active'>Active</label>) ||
                                            (service.status === 2 && <label className='status status-close'>Close</label>) ||
                                            (service.status === 3 && <label className='status status-pause'>Pause</label>)}
                                    </td>
                                    <td>
                                        <Menu menuButton={<MenuButton className='btn-action'><BsThreeDotsVertical /></MenuButton>} transition>
                                            <MenuItem>Edit</MenuItem>
                                            <MenuItem>Delete</MenuItem>
                                        </Menu>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
                <div className='d-flex float-end padging'>
                    {numberOfPage.map((item) => (
                        <label onClick={(e) => setNumberPage(e.target.textContent)}>{item}</label>
                    ))}
                </div>
            </div>
        </div>
    )
}

export default memo(PartnerHomeHadService)
