import { useState, memo } from 'react'
import './RegisterInformationRooms.scss'
import { vietnamese, english } from '../../Languages/InputNameInformationService'
import { vietnameseRoomType, englishRoomType } from '../../Languages/RoomType'

function RegisterInformationRooms({ languageSelected }) {
    const [roomName, setRoomName] = useState('')
    const [roomType, setRoomType] = useState('')
    const [numberOfRoom, setNumberOfRoom] = useState(0)
    const [bedType, setBedType] = useState('')
    const [description, setDescriptions] = useState('')

    let languageList = (languageSelected === 'EN' ? english : vietnamese)[0][1]
    let roomTypeList = (languageSelected === 'EN' ? englishRoomType : vietnameseRoomType)

    const handleBlur = (input) => {
        if (input.value != '') {
            input.style.border = 'solid 1px #4874E8'
        }
        else {
            input.style.border = 'solid 1px #D9D9D9'
        }
    }

    const handleFocus = (input) => {
        input.style.border = 'solid 1px #4874E8'
    }

    return (
        <div className='container'>
            <div className='space-30 background-w'>
                <div className="line-input">
                    <div className="input-alone">
                        <label htmlFor='roomName' className='d-block'>{languageList.txtRoomName}</label>
                        <input type="text" id='roomName' className='input-inline'
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            onChange={(e) => setRoomName(e.target.value)} />
                    </div>
                </div>
                <div className="line-input">
                    <div className=" input-alone">
                        <label htmlFor='roomType' className='d-block'>{languageList.txtRoomType}<span className="requird-star">*</span></label>
                        <select type="text" id='roomType' className='input-inline had-input'
                            onChange={(e) => setRoomName(e.target.value)}>
                            {roomTypeList.map((item) => (
                                <option key={item} value={item}>{item}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className="input-alone">
                        <label htmlFor='roomName' className='d-block'>{languageList.txtRoomName}</label>
                        <input type="text" id='roomName' className='input-inline'
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            onChange={(e) => setRoomName(e.target.value)} />
                    </div>
                </div>
                <div className="d-flex line-input">
                    <div className='w-45'>
                        <label htmlFor="companyName" className="d-block">{languageList.txtNumberOfRooms}<span className="requird-star">*</span></label>
                        <label htmlFor="companyName" className="d-block size-12"><span className="requird-star">*</span>{languageList.warningAboutNumberOfRooms}</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            id='companyName' name='companyName' className="input-inline" type='text'
                            onChange={(e) => setRoomName(e.target.value)} />
                    </div>
                    <div className='w-45'>
                        <label htmlFor="companyAbbreviation" className="d-block">{ }</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            id='companyAbbreviation'
                            name='companyAbbreviation' className="input-inline" type='text'
                            onChange={(e) => setRoomName(e.target.value)} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default memo(RegisterInformationRooms)
