import { memo, useState } from 'react'
import { Link } from 'react-router-dom'
import './PartnerHomeHadService.scss'
import { AiOutlineSearch, AiOutlinePlus } from 'react-icons/ai'
import Select from 'react-select';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';

function PartnerHomeHadService() {
    const [anchorEl, setAnchorEl] = useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };

    const [status, setStatus] = useState(0)
    const options = [
        { value: 0, label: 'All' },
        { value: 1, label: 'Active' },
        { value: 2, label: 'Close' },
        { value: 3, label: 'Pause' }
    ]

    const listService = [
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 3
        },
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 2
        },
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        },
        {
            name: 'Sóng xanh',
            address: 'Hà Đông, Phố Mộ Lao, Hà Nội',
            status: 1
        }
    ]

    return (
        <div className='home-partner-main d-flex'>
            <div className='menu'>
                asdds
            </div>
            <div className='container list-service-main'>
                <div className='d-flex tool-list-service-main'>
                    <div className='d-flex list-service-tool'>
                        <label htmlFor='search-name' className='search-input-text'>
                            <AiOutlineSearch className='icon-inner icon-search-list-service' />
                            <input id='search-name' type='text' className='input-inline input-list-service' />
                        </label>
                        <Select placeholder='Status' isSearchable={false} className='input-list-service select-search-service' options={options} />
                        <Select placeholder='???' isSearchable={false} className='input-list-service select-search-service' options={options} />
                    </div>
                    <Link to='/partner/select-detail-service' className='btn btn-primary btn-add-new-service'><AiOutlinePlus /> Add new</Link>
                </div>
                <table className='table table-hover'>
                    <thead>
                        <tr>
                            <td>STT</td>
                            <td>Name</td>
                            <td>Address</td>
                            <td>Status</td>
                            <td>Action</td>
                        </tr>
                    </thead>
                    <tbody>
                        {listService.map((service, index) => (
                            <tr>
                                <td>{index + 1}</td>
                                <td>{service.name}</td>
                                <td>{service.address}</td>
                                {(service.status === 1 && <td className='status-active'>Active</td>) ||
                                    (service.status === 2 && <td className='status-close'>Close</td>) ||
                                    (service.status === 3 && <td className='status-pause'>Pause</td>)}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default memo(PartnerHomeHadService)
