import { memo, useState } from 'react'
import { english, vietnamese } from '../../Languages/TourSchedule'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './TourSchedule.scss'
import Select from 'react-select';
import { BsChevronUp } from 'react-icons/bs'

function TourSchedule({ languageSelected, tourSchedule, setTourSchedule }) {
    const languageList = (languageSelected === 'EN' ? english : vietnamese)

    const handleBlur = (input) => {
        input.style.border = 'solid 1px #D9D9D9'
    }

    const handleFocus = (input) => {
        input.style.border = 'solid 1px #4874E8'
    }

    const [serviceList, setServiceList] = useState([
        {
            value: 1,
            label: 'Hotel abc'
        },
        {
            value: 2,
            label: 'Entertainment abc'
        },
        {
            value: 3,
            label: 'Restaurant abc'
        },
        {
            value: 4,
            label: 'Hotel abc'
        },
        {
            value: 5,
            label: 'Entertainment abc'
        },
        {
            value: 6,
            label: 'Restaurant abc'
        },
        {
            value: 7,
            label: 'Hotel abc'
        },
        {
            value: 8,
            label: 'Entertainment abc'
        },
        {
            value: 9,
            label: 'Restaurant abc'
        },
        {
            value: 10,
            label: 'Hotel abc'
        },
        {
            value: 11,
            label: 'Entertainment abc'
        },
        {
            value: 12,
            label: 'Restaurant abc'
        }
    ])

    const modules = {
        toolbar: [
            ['bold', 'italic', 'underline', 'strike'],
            ['blockquote', 'code-block'],
            [{ 'header': 1 }, { 'header': 2 }],
            [{ 'list': 'ordered' }, { 'list': 'bullet' }],
            [{ 'script': 'sub' }, { 'script': 'super' }],
            [{ 'indent': '-1' }, { 'indent': '+1' }],
            [{ 'direction': 'rtl' }],
            [{ 'size': ['small', false, 'large', 'huge'] }],
            [{ 'header': [1, 2, 3, 4, 5, 6, false] }],
            [{ 'color': [] }, { 'background': [] }],
            [{ 'font': [] }],
            [{ 'align': [] }],
            ['clean'],
        ]
    }

    const formats = [
        'header', 'font', 'background', 'color', 'code', 'size',
        'bold', 'italic', 'underline', 'strike', 'blockquote',
        'list', 'bullet', 'indent', 'script', 'align', 'direction',
        'link', 'image', 'code-block', 'formula', 'video'
    ]

    const handleOpenServices = ((index) => {
        let newDataForm = [...tourSchedule]
        newDataForm[index].openServices = true
        setTourSchedule(newDataForm)
    })

    const handleCloseServices = ((index) => {
        let newDataForm = [...tourSchedule]
        newDataForm[index].openServices = false
        setTourSchedule(newDataForm)
    })

    return (
        <div>
            {
                [...tourSchedule].map((item, index) => (
                    <div className={`form-tour-schedule bg-white ${item.openServices && 'pb-200'}`}>
                        <label className='title'> {languageList.txtDay} {index + 1} </label>
                        <div className='space-icon-show-container bd-t btn-show-hide-overflow'><BsChevronUp className='icon-show-container' /></div>
                        <div className="d-flex line-input">
                            <div className="mlr-50 input-alone">
                                <label className="d-block title-create-tour">{languageList.txtName}</label>
                                <input onFocus={(e) => handleFocus(e.target)}
                                    onBlur={(e) => handleBlur(e.target)}
                                    value={item.name}
                                    className="input-inline " type='text' />
                            </div>
                        </div>
                        <div className="d-flex line-input mb-20">
                            <div className="mlr-50 input-alone">
                                <label className="d-block title-create-tour">{languageList.txtContent}</label>
                                <ReactQuill defaultValue={item.content}
                                    className="input-inline border-none quill-create-tour"
                                    theme="snow" modules={modules} formats={formats} />
                            </div>
                        </div>
                        <div className="d-flex line-input">
                            <div className="mlr-50 input-alone">
                                <label className="d-block title-create-tour">{languageList.txtDestination}</label>
                                <input onFocus={(e) => handleFocus(e.target)}
                                    onBlur={(e) => handleBlur(e.target)}
                                    value={item.toPlace}
                                    className="input-inline" type='text' />
                            </div>
                        </div>
                        <div className='d-flex line-input'>
                            <div className="mlr-50 input-alone">
                                <label className="d-block title-create-tour">{languageList.txtServices}</label>
                                <Select onMenuOpen={() => handleOpenServices(index)}
                                    onMenuClose={() => handleCloseServices(index)}
                                    className="input-inline border-none" placeholder={languageList.txtServices}
                                    closeMenuOnSelect={false}
                                    options={serviceList} isMulti />
                            </div>
                        </div>
                        <div className='space-icon-show-container btn-show-hide-overflow-down'><BsChevronUp className='icon-show-container' /></div>
                    </div>
                ))
            }
        </div>
    )
}

export default memo(TourSchedule)
