import { storage } from '../Upload'
import { ref, uploadBytes, listAll, getDownloadURL, getStream } from 'firebase/storage'
export const UploadImage = async (imageList, path, count, countUploaded, setCountUploaded, id, backPath, index, lastPath) => {
    const image = [...imageList]
    let pathIndex = `${lastPath}`
    if (index > 0) {
        pathIndex = `${index}/${lastPath}`
    }
    console.log(pathIndex)
    for (let i = 0; i < imageList.length; i++) {
        const imageService = ref(storage, `${path}/${id}/${backPath}/${pathIndex}/${image[i].name}`);
        uploadBytes(imageService, image[i]).then(() => {
            setCountUploaded(pre => pre + 1)
            console.log(countUploaded)
            if (countUploaded === count) {
                console.log('xong')
            }
        })
    }
}
