import { vietnamese, english } from "../../Languages/OptionInputInformationService";
import { useState, memo, useEffect, useRef } from 'react'
import { useNavigate } from "react-router-dom";
import './RegisterInformationServiceLayout.scss'
import RegisterInformationRooms from '../../pages/RegisterInformationRooms'
import RegisterInformationAccomodation from '../../pages/RegisterInformationAccomodation'
import RegisterUtilitiesAndServices from "../../pages/RegisterUtilitiesAndServices";
import RegisterPhotos from "../../pages/RegisterPhotos";
import RegisterInformationEntertainment from "../../pages/RegisterInformationEntertainment";
import RegisterInformationRestaurant from "../../pages/RegisterInformationRestaurant";
import axios from "axios";
import LoadingDialog from "../LoadingDialog";
import { toast } from 'react-toastify'
import { UploadImage } from "../../../firebase/UploadImage";


function RegisterInformationServiceLayout({ setProgress, languageSelected }) {
    const navigate = useNavigate()

    const [showLoading, setShowLoading] = useState(false);
    const [moveToTop, setMoveToTop] = useState(true)

    useEffect(() => {
        window.scrollTo(0, 0)
    }, [moveToTop])

    let languageList = (languageSelected === 'EN' ? english : vietnamese)
    const categoryService = sessionStorage.getItem('index-service-selected')
    const categoryDetailService = sessionStorage.getItem('detail-service')
    const [selectedOption, setSelectedOption] = useState(0)

    //form input information
    const [propertyName, setPropertyName] = useState('')
    const [status, setStatus] = useState(1)
    const [starRating, setStarRating] = useState(1)
    const [city, setCity] = useState('Hà Nội')
    const [address, setAddress] = useState('')
    const [website, setWebsite] = useState('')
    const [taxCode, setTaxCode] = useState('')
    const [description, setDescription] = useState('')
    const [fax, setFax] = useState('')
    const [phoneNumberContact, setPhoneNumberContact] = useState('')
    const [emailContact, setEmailContact] = useState('')

    //feild for accomodation
    const [numberOfFloors, setNumberOfFloors] = useState(0)

    //feild for restaurant and entertainment
    const [timeOpen, setTimeOpen] = useState('')
    const [timeClose, setTimeClose] = useState('')

    //feild for restaurant
    const [typeOfCuisine, setTypeOfCuisine] = useState([])

    //feild for entertainment
    const [dateOpen, setDateOpen] = useState([])
    const [childTiketPrice, setChildTiketPrice] = useState(0)
    const [adultTicketPrice, setAdultTicketPrice] = useState(0)

    //form input rooms
    const [roomsData, setroomsData] = useState([
        {
            showHide: true,
            roomName: '',
            roomType: 0,
            numberOfRoom: 0,
            bedType: 0,
            numberOfPeopleRoom: 0,
            roomSize: 0,
            description: '',
            bedRoomPhoto: [],
            wcPhoto: [],
            otherPhoto: []
        }
    ])

    //Utilities and services
    const [utilitiesInternet, setUtilitiesInternet] = useState([])
    const [utilitiesFacilities, setUtilitiesFacilities] = useState([])
    const [utilitiesAccomodationService, setUtilitiesAccomodationService] = useState([])
    const [utilitiesBeautyRelaxation, setUtilitiesBeautyRelaxation] = useState([])
    const [utilitiesFoodService, setUtilitiesFoodService] = useState([])
    const [utilitiesEntertainment, setUtilitiesEntertainment] = useState([])
    const [utilitiesChildrenService, setUtilitiesChildrenService] = useState([])
    const [utilitiesShopping, setUtilitiesShopping] = useState([])
    const [utilitiesBusinessService, setUtilitiesBusinessService] = useState([])
    const [utilitiesVehicle, setUtilitiesVehicle] = useState([])

    //on off star rating
    const [onStarRating, setOnStarRating] = useState(true)

    //Photos
    const [receptionHallPhoto, setReceptionHallPhoto] = useState([])
    const [outdoorPhoto, setOutdoorPhoto] = useState([])
    const [otherPhotoAccomodation, setOtherPhotoAccomodation] = useState([])

    //
    const count = useRef(-1)
    const [leng, setLeng] = useState(0)
    useEffect(() => {
        if (leng === count.current) {
            count.current = -1;
            setLeng(0)
            navigate('/partner')
        }
    }, [leng])

    const handleClickSaveAndNext = () => {
        setMoveToTop(pre => !pre)
        setSelectedOption(pre => pre + 1)
    }

    //Submit accomodation
    const checkInformationRooms = () => {
        for (let i = 0; i < roomsData.length; i++) {
            if (roomsData[i].numberOfRoom == 0 || roomsData[i].numberOfPeopleRoom == 0 || roomsData[i].roomSize == 0) {
                return false;
            }
        }
        return true
    }

    //Submit Accomodation Service
    const handleClickSubmitAccomodation = async () => {
        var regexWeb = /[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_\+.~#?&//=]*)?/gi;
        var regexWebsite = new RegExp(regexWeb);

        if (propertyName === '' || address === '' || taxCode === '' || phoneNumberContact === '' || emailContact === '' || !checkInformationRooms) {
            toast.warning(languageList[4])
        }
        else if (propertyName !== '' && address !== '' && (website === '' || website.match(regexWebsite)) && taxCode !== ''
            && phoneNumberContact.match(/\d/g).length === 10 && emailContact !== ''
            && /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(emailContact) && checkInformationRooms) {
            setShowLoading(true)
            //Submit
            const accommodation = {
                "serviceDTO":
                {
                    "serviceName": propertyName,
                    "fax": fax,
                    "phone": phoneNumberContact,
                    "email": emailContact,
                    "address": address,
                    "city": city,
                    "link": website,
                    "status": status,
                    "taxCode": taxCode,
                    "serviceCategory": sessionStorage.getItem('index-service-selected'),
                    "partnerEmail": sessionStorage.getItem('email')
                },
                "accommodationType": sessionStorage.getItem('detail-service'),
                "rate": (onStarRating ? starRating : 0),
                "numberFloors": numberOfFloors,
                "description": description,
                "roomDTOList": roomsData
            }

            await axios.post('http://localhost:8081/wetravel/create/accommodation', accommodation)
                .then((res) => {
                    const serviceId = res.data.data.serviceDTO.serviceId
                    //Anh
                    count.current = receptionHallPhoto.length + outdoorPhoto.length + otherPhotoAccomodation.length
                    for (let j = 0; j < roomsData.length; j++) {
                        count.current += (roomsData[j].bedRoomPhoto.length
                            + roomsData[j].wcPhoto.length
                            + roomsData[j].otherPhoto.length)
                    }
                    UploadImage(receptionHallPhoto, 'service/accomodation', count, setLeng, serviceId, 'information', 0, 'receptionHallPhoto')
                    UploadImage(outdoorPhoto, 'service/accomodation', count, setLeng, serviceId, 'information', 0, 'outdoorPhoto')
                    UploadImage(otherPhotoAccomodation, 'service/accomodation', count, setLeng, serviceId, 'information', 0, 'otherPhotoAccomodation')
                    for (let i = 0; i < roomsData.length; i++) {
                        UploadImage(roomsData[i].bedRoomPhoto, 'service/accomodation', count, setLeng, serviceId, 'room', i + 1, 'bedRoomPhoto')
                        UploadImage(roomsData[i].wcPhoto, 'service/accomodation', count, setLeng, serviceId, 'room', i + 1, 'wcPhoto')
                        UploadImage(roomsData[i].otherPhoto, 'service/accomodation', count, setLeng, serviceId, 'room', i + 1, 'otherPhoto')
                    }
                })
                .catch((e) => {
                    console.log(e)
                })
            setShowLoading(false)
        }
        else {
            toast.warning(languageList[5])
        }
    }

    //Submit Entertainment Service
    const handleClickSubmitEntertainment = async () => {
        var regexWeb = /[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_\+.~#?&//=]*)?/gi;
        var regexWebsite = new RegExp(regexWeb);

        if (propertyName === '' || address === '' || taxCode === '' || phoneNumberContact === '' || emailContact === '' ||
            timeOpen === '' || timeClose === '' || dateOpen.length == 0) {
            toast.warning(languageList[4])
        }
        else if (propertyName !== '' && address !== '' && (website === '' || website.match(regexWebsite))
            && taxCode !== '' && phoneNumberContact.match(/\d/g).length === 10 && emailContact !== ''
            && /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(emailContact)) {
            setShowLoading(true)
            //Submit
            const entertainment = {
                "serviceDTO":
                {
                    "serviceName": propertyName,
                    "fax": fax,
                    "phone": phoneNumberContact,
                    "email": emailContact,
                    "address": address,
                    "city": city,
                    "link": website,
                    "status": status,
                    "taxCode": taxCode,
                    "serviceCategory": sessionStorage.getItem('index-service-selected'),
                    "partnerEmail": sessionStorage.getItem('email')
                },

                entertainmentType: sessionStorage.getItem('detail-service'),
                dowOpen: dateOpen.join(" "),
                timeOpen: timeOpen,
                timeClose: timeClose,
                priceTicket: childTiketPrice
            }


            await axios.post('http://localhost:8081/wetravel/create/entertainment', entertainment)
                .then((res) => {
                    const serviceId = res.data.data.serviceDTO.serviceId
                    //Anh
                    count.current = receptionHallPhoto.length + outdoorPhoto.length + otherPhotoAccomodation.length
                    for (let j = 0; j < roomsData.length; j++) {
                        count.current += (roomsData[j].bedRoomPhoto.length
                            + roomsData[j].wcPhoto.length
                            + roomsData[j].otherPhoto.length)
                    }
                    UploadImage(receptionHallPhoto, 'service/entertainment', count, setLeng, serviceId, 'information', 0, 'receptionHallPhoto')
                    UploadImage(outdoorPhoto, 'service/entertainment', count, setLeng, serviceId, 'information', 0, 'outdoorPhoto')
                    UploadImage(otherPhotoAccomodation, 'service/entertainment', count, setLeng, serviceId, 'information', 0, 'otherPhotoAccomodation')
                })
                .catch((e) => {
                    console.log(e)
                })
            setShowLoading(false)
        }
        else {
            toast.warning(languageList[5])
        }
    }

    //Submit Restaurants Services
    const handleClickSubmitRestaurant = async () => {
        var regexWeb = /[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_\+.~#?&//=]*)?/gi;
        var regexWebsite = new RegExp(regexWeb);

        if (propertyName === '' || address === '' || taxCode === '' || phoneNumberContact === '' || emailContact === '' || typeOfCuisine.length == 0 ||
            timeOpen === '' || timeClose === '') {
            toast.warning(languageList[4])
        }
        else if (propertyName !== '' && address !== '' && (website === '' || website.match(regexWebsite)) && typeOfCuisine.length > 0
            && taxCode !== '' && phoneNumberContact.match(/\d/g).length === 10 && emailContact !== ''
            && /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(emailContact) && checkInformationRooms) {
            setShowLoading(true)
            //Submit
            const restaurant = {
                "serviceDTO": {
                    "serviceName": propertyName,
                    "fax": fax,
                    "phone": phoneNumberContact,
                    "email": emailContact,
                    "address": address,
                    "city": city,
                    "link": website,
                    "status": status,
                    "taxCode": taxCode,
                    "serviceCategory": sessionStorage.getItem('index-service-selected'),
                    "partnerEmail": sessionStorage.getItem('email')
                },
                restaurantServiceType: sessionStorage.getItem('detail-service'),
                timeOpen: timeOpen,
                timeClose: timeClose,
                description: description,
                rate: (onStarRating ? starRating : 0)
            }

            await axios.post('http://localhost:8081/wetravel/create/restaurant', restaurant)
                .then((res) => {
                    const serviceId = res.data.data.serviceDTO.serviceId
                    //Anh
                    count.current = receptionHallPhoto.length + outdoorPhoto.length + otherPhotoAccomodation.length
                    UploadImage(receptionHallPhoto, 'service/restaurant', count, setLeng, serviceId, 'information', 0, 'receptionHallPhoto')
                    UploadImage(outdoorPhoto, 'service/restaurant', count, setLeng, serviceId, 'information', 0, 'outdoorPhoto')
                    UploadImage(otherPhotoAccomodation, 'service/restaurant', count, setLeng, serviceId, 'information', 0, 'otherPhotoAccomodation')
                })
                .catch((e) => {
                })
            setShowLoading(false)
        }
        else {
            toast.warning(languageList[5])
        }
    }

    const numberOfOption = [
        [<RegisterInformationAccomodation languageSelected={languageSelected}
            handleClickSaveAndNext={handleClickSaveAndNext}
            propertyName={propertyName} setPropertyName={setPropertyName}
            status={status} setStatus={setStatus} numberOfFloors={numberOfFloors}
            setNumberOfFloors={setNumberOfFloors} starRating={starRating} setStarRating={setStarRating}
            city={city} setCity={setCity} address={address} setAddress={setAddress}
            description={description} setDescription={setDescription} fax={fax} setFax={setFax}
            phoneNumberContact={phoneNumberContact} setPhoneNumberContact={setPhoneNumberContact}
            emailContact={emailContact} setEmailContact={setEmailContact} website={website} setWebsite={setWebsite}
            taxCode={taxCode} setTaxCode={setTaxCode} onStarRating={onStarRating} setOnStarRating={setOnStarRating}
        />,
        <RegisterInformationRooms languageSelected={languageSelected} formFields={roomsData} setFormFields={setroomsData}
            handleClickSaveAndNext={handleClickSaveAndNext} />,
        <RegisterUtilitiesAndServices languageSelected={languageSelected} handleClickSaveAndNext={handleClickSaveAndNext}
            utilitiesInternet={utilitiesInternet} setUtilitiesInternet={setUtilitiesInternet}
            utilitiesFacilities={utilitiesFacilities} setUtilitiesFacilities={setUtilitiesFacilities}
            utilitiesAccomodationService={utilitiesAccomodationService} setUtilitiesAccomodationService={setUtilitiesAccomodationService}
            utilitiesBeautyRelaxation={utilitiesBeautyRelaxation} setUtilitiesBeautyRelaxation={setUtilitiesBeautyRelaxation}
            utilitiesFoodService={utilitiesFoodService} setUtilitiesFoodService={setUtilitiesFoodService}
            utilitiesEntertainment={utilitiesEntertainment} setUtilitiesEntertainment={setUtilitiesEntertainment}
            utilitiesChildrenService={utilitiesChildrenService} setUtilitiesChildrenService={setUtilitiesChildrenService}
            utilitiesShopping={utilitiesShopping} setUtilitiesShopping={setUtilitiesShopping}
            utilitiesBusinessService={utilitiesBusinessService} setUtilitiesBusinessService={setUtilitiesBusinessService}
            utilitiesVehicle={utilitiesVehicle} setUtilitiesVehicle={setUtilitiesVehicle} />,
        <RegisterPhotos languageSelected={languageSelected} receptionHallPhoto={receptionHallPhoto} setReceptionHallPhoto={setReceptionHallPhoto}
            outdoorPhoto={outdoorPhoto} setOutdoorPhoto={setOutdoorPhoto} otherPhotoAccomodation={otherPhotoAccomodation}
            setOtherPhotoAccomodation={setOtherPhotoAccomodation} handleClickSubmit={handleClickSubmitAccomodation} />],
        [<RegisterInformationEntertainment languageSelected={languageSelected}
            handleClickSaveAndNext={handleClickSaveAndNext}
            propertyName={propertyName} setPropertyName={setPropertyName}
            dateOpen={dateOpen} setDateOpen={setDateOpen}
            childTiketPrice={childTiketPrice} setChildTiketPrice={setChildTiketPrice}
            adultTicketPrice={adultTicketPrice} setAdultTicketPrice={setAdultTicketPrice}
            status={status} setStatus={setStatus}
            city={city} setCity={setCity} address={address} setAddress={setAddress}
            description={description} setDescription={setDescription} fax={fax} setFax={setFax}
            phoneNumberContact={phoneNumberContact} setPhoneNumberContact={setPhoneNumberContact}
            emailContact={emailContact} setEmailContact={setEmailContact} website={website} setWebsite={setWebsite}
            taxCode={taxCode} setTaxCode={setTaxCode} onStarRating={onStarRating} setOnStarRating={setOnStarRating} timeOpen={timeOpen}
            setTimeOpen={setTimeOpen} timeClose={timeClose} setTimeClose={setTimeClose} />,
        <RegisterUtilitiesAndServices languageSelected={languageSelected} handleClickSaveAndNext={handleClickSaveAndNext}
            utilitiesInternet={utilitiesInternet} setUtilitiesInternet={setUtilitiesInternet}
            utilitiesFacilities={utilitiesFacilities} setUtilitiesFacilities={setUtilitiesFacilities}
            utilitiesAccomodationService={utilitiesAccomodationService} setUtilitiesAccomodationService={setUtilitiesAccomodationService}
            utilitiesBeautyRelaxation={utilitiesBeautyRelaxation} setUtilitiesBeautyRelaxation={setUtilitiesBeautyRelaxation}
            utilitiesFoodService={utilitiesFoodService} setUtilitiesFoodService={setUtilitiesFoodService}
            utilitiesEntertainment={utilitiesEntertainment} setUtilitiesEntertainment={setUtilitiesEntertainment}
            utilitiesChildrenService={utilitiesChildrenService} setUtilitiesChildrenService={setUtilitiesChildrenService}
            utilitiesShopping={utilitiesShopping} setUtilitiesShopping={setUtilitiesShopping}
            utilitiesBusinessService={utilitiesBusinessService} setUtilitiesBusinessService={setUtilitiesBusinessService}
            utilitiesVehicle={utilitiesVehicle} setUtilitiesVehicle={setUtilitiesVehicle} />,
        <RegisterPhotos languageSelected={languageSelected} receptionHallPhoto={receptionHallPhoto} setReceptionHallPhoto={setReceptionHallPhoto}
            outdoorPhoto={outdoorPhoto} setOutdoorPhoto={setOutdoorPhoto} otherPhotoAccomodation={otherPhotoAccomodation}
            setOtherPhotoAccomodation={setOtherPhotoAccomodation} handleClickSubmit={handleClickSubmitEntertainment} />],
        [<RegisterInformationRestaurant languageSelected={languageSelected}
            handleClickSaveAndNext={handleClickSaveAndNext}
            propertyName={propertyName} setPropertyName={setPropertyName}
            typeOfCuisine={typeOfCuisine} setTypeOfCuisine={setTypeOfCuisine}
            status={status} setStatus={setStatus} starRating={starRating} setStarRating={setStarRating}
            city={city} setCity={setCity} address={address} setAddress={setAddress}
            description={description} setDescription={setDescription} fax={fax} setFax={setFax}
            phoneNumberContact={phoneNumberContact} setPhoneNumberContact={setPhoneNumberContact}
            emailContact={emailContact} setEmailContact={setEmailContact} website={website} setWebsite={setWebsite}
            taxCode={taxCode} setTaxCode={setTaxCode} onStarRating={onStarRating} setOnStarRating={setOnStarRating} timeOpen={timeOpen}
            setTimeOpen={setTimeOpen} timeClose={timeClose} setTimeClose={setTimeClose} />,
        <RegisterUtilitiesAndServices languageSelected={languageSelected} handleClickSaveAndNext={handleClickSaveAndNext}
            utilitiesInternet={utilitiesInternet} setUtilitiesInternet={setUtilitiesInternet}
            utilitiesFacilities={utilitiesFacilities} setUtilitiesFacilities={setUtilitiesFacilities}
            utilitiesAccomodationService={utilitiesAccomodationService} setUtilitiesAccomodationService={setUtilitiesAccomodationService}
            utilitiesBeautyRelaxation={utilitiesBeautyRelaxation} setUtilitiesBeautyRelaxation={setUtilitiesBeautyRelaxation}
            utilitiesFoodService={utilitiesFoodService} setUtilitiesFoodService={setUtilitiesFoodService}
            utilitiesEntertainment={utilitiesEntertainment} setUtilitiesEntertainment={setUtilitiesEntertainment}
            utilitiesChildrenService={utilitiesChildrenService} setUtilitiesChildrenService={setUtilitiesChildrenService}
            utilitiesShopping={utilitiesShopping} setUtilitiesShopping={setUtilitiesShopping}
            utilitiesBusinessService={utilitiesBusinessService} setUtilitiesBusinessService={setUtilitiesBusinessService}
            utilitiesVehicle={utilitiesVehicle} setUtilitiesVehicle={setUtilitiesVehicle} />,
        <RegisterPhotos languageSelected={languageSelected} receptionHallPhoto={receptionHallPhoto} setReceptionHallPhoto={setReceptionHallPhoto}
            outdoorPhoto={outdoorPhoto} setOutdoorPhoto={setOutdoorPhoto} otherPhotoAccomodation={otherPhotoAccomodation}
            setOtherPhotoAccomodation={setOtherPhotoAccomodation} handleClickSubmit={handleClickSubmitRestaurant} />]]

    const handleSelectAnOpion = (item) => {
        setSelectedOption(item)
    }

    return (
        <>
            <div className="detail-service">
                <div className="container d-flex space-register-information-service">
                    <div className="options">
                        {numberOfOption[categoryService - 1].map((item, index) => (<>
                            <div key={index} className={`option ${selectedOption === index && 'selected-option'}`}
                                onClick={() => handleSelectAnOpion(index)}
                            >{languageList[categoryService - 1][index]}
                            </div>
                        </>
                        ))}
                    </div>
                    <div className="content-register-information-service">
                        {numberOfOption[categoryService - 1][selectedOption]}
                    </div>
                </div>
            </div>
            {showLoading && <div className='popup'>
                <div className='bg-popup' />
                {showLoading && <LoadingDialog />}
            </div>}
        </>
    )
}

export default memo(RegisterInformationServiceLayout)
