import { vietnameseBedType, englishBedType } from '../../Languages/BedType'
import { TbCameraPlus } from 'react-icons/tb'
import { BsTrash, BsArrowRight } from 'react-icons/bs'
import { toast } from 'react-toastify'

function RegisterInformationRooms({ languageSelected, roomName, setRoomName, roomType, setRoomType, roomSize, setRoomSize,
    bedType, setBedType, description, setDescriptions, numberOfRoom, setNumberOfRoom, numberOfPeopleRoom, setNumberOfPeopleRoom,
    bedRoomPhoto, setBedRoomPhoto, wcPhoto, setWCPhoto, otherPhoto, setOtherPhoto, setSelectedOption, setMoveToTop }) {
    let languageList = (languageSelected === 'EN' ? english : vietnamese)[0][1]
    let roomTypeList = (languageSelected === 'EN' ? englishRoomType : vietnameseRoomType)
    let bedTypeList = (languageSelected === 'EN' ? englishBedType : vietnameseBedType)

    const handleBlur = (input) => {
        input.style.border = 'solid 1px #D9D9D9'
    }

    const handleFocus = (input) => {
        input.style.border = 'solid 1px #4874E8'
    }

    const handleBedRoomPhoto = (data) => {
        let listImage = [...bedRoomPhoto]
        for (let i = 0; i < data.length; i++) {
            listImage.push(data[i])
        }
        setBedRoomPhoto(listImage)
    }

    const handleWCPhoto = (data) => {
        let listImage = [...wcPhoto]
        for (let i = 0; i < data.length; i++) {
            listImage.push(data[i])
        }
        setWCPhoto(listImage)
    }

    const handleOtherPhoto = (data) => {
        let listImage = [...otherPhoto]
        for (let i = 0; i < data.length; i++) {
            listImage.push(data[i])
        }
        setOtherPhoto(listImage)
    }

    const handleDeleteBedRoomImage = (index) => {
        let afterRemove = []
        for (let i = 0; i < bedRoomPhoto.length; i++) {
            if (i != index) {
                afterRemove.push(bedRoomPhoto[i])
            }
        }
        setBedRoomPhoto(afterRemove)
    }

    const handleDeleteWCImage = (index) => {
        let afterRemove = []
        for (let i = 0; i < wcPhoto.length; i++) {
            if (i != index) {
                afterRemove.push(wcPhoto[i])
            }
        }
        setWCPhoto(afterRemove)
    }

    const handleDeleteOtherImage = (index) => {
        let afterRemove = []
        for (let i = 0; i < otherPhoto.length; i++) {
            if (i != index) {
                afterRemove.push(otherPhoto[i])
            }
        }
        setOtherPhoto(afterRemove)
    }

    const handleClickSaveAndNext = () => {
        setMoveToTop(pre => !pre)
        setSelectedOption(pre => pre + 1)
    }

    return (
        <div className='container'>
            <div className='space-30 background-w'>
                <div className="line-input">
                    <div className="input-alone">
                        <label htmlFor='roomName' className='d-block'>{languageList.txtRoomName}</label>
                        <input value={roomName} id='roomName' className='input-inline'
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            onChange={(e) => setRoomName(e.target.value)} />
                    </div>
                </div>
                <div className="line-input">
                    <div className=" input-alone">
                        <label htmlFor='roomType' className='d-block'>{languageList.txtRoomType}<span className="requird-star">*</span></label>
                        <select id='roomType' className='input-inline had-input'
                            onChange={(e) => setRoomType(e.target.value)}
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}>
                            {roomTypeList.map((item, index) => (
                                <option selected={index == roomType} key={item} value={index}>{item}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="d-flex line-input line-input-3-line">
                    <div className='w-45'>
                        <label htmlFor="numberOfRoom" className="d-block">{languageList.txtNumberOfRooms}<span className="requird-star">*</span></label>
                        <label htmlFor="numberOfRoom" className="d-block size-12"><span className="requird-star">*</span>{languageList.warningAboutNumberOfRooms}</label>
                        <input value={numberOfRoom} onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            id='numberOfRoom' className="input-inline" type='number'
                            onChange={(e) => setNumberOfRoom(e.target.value)} />
                    </div>
                    <div className='w-45'>
                        <label htmlFor="bedType" className="d-block">{languageList.txtBedType}<span className="requird-star">*</span></label>
                        <select id='bedType' className='input-inline had-input'
                            onChange={(e) => setBedType(e.target.value)}
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}>
                            {bedTypeList.map((item, index) => (
                                <option selected={index == bedType} key={item} value={index}>{item}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="d-flex line-input line-input-3-line">
                    <div className='w-45'>
                        <label htmlFor="numberOfPeopleOnRoom" className="d-block">{languageList.txtNumberOfPeopleOnRoom}<span className="requird-star">*</span></label>
                        <input value={numberOfPeopleRoom} onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            id='numberOfPeopleOnRoom' className="input-inline" type='number'
                            onChange={(e) => setNumberOfPeopleRoom(e.target.value)} />
                    </div>
                    <div className='w-45'>
                        <label htmlFor="roomSize" className="d-block">{languageList.txtRoomSize}<span className="requird-star">*</span></label>
                        <input value={roomSize} onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            id='roomSize' className="input-inline" type='number'
                            onChange={(e) => setRoomSize(e.target.value)} />
                    </div>
                </div>
                <div className="line-input">
                    <div className="input-alone">
                        <label htmlFor='description' className='d-block'>{languageList.txtDescription}</label>
                        <textarea value={description} rows="4" id='description' className='input-inline'
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            onChange={(e) => setDescriptions(e.target.value)} />
                    </div>
                </div>
            </div>
            <div className='space-30 background-w mt-30'>
                <label className='title'>{languageList.txtImage}</label>
                <div className='container'>
                    <div>
                        <label className='d-block mb-20'>{languageList.txtBedRoomPhoto}</label>
                        <div className='grid-image'>
                            {[...bedRoomPhoto].map((img, index) => (
                                <div className="image-selected" id={`${index}`}>
                                    <div className='layer-delete' onClick={() => handleDeleteBedRoomImage(index)}>
                                        <div className='check-delete-image' />
                                        <BsTrash className='image-trash' />
                                    </div>
                                    <img className="image-selected" src={URL.createObjectURL(img)} />
                                </div>
                            ))}
                            <label htmlFor='bedRoomImage' className='add-image-button'>
                                <div className="select-image-service" >
                                    <TbCameraPlus className='icon-camera-plus' />
                                    <div className='text-camera-plus d-block'>{languageList.txtAddImage}</div>
                                </div>
                                <input onChangeCapture={(e) => handleBedRoomPhoto(e.target.files)} type="file" id="bedRoomImage" accept="image/*" className='select-image-hide' multiple />
                            </label>
                        </div>
                    </div>
                    <div className='mt-30'>
                        <label className='d-block mb-20'>{languageList.txtWCPhoto}</label>
                        <div className='grid-image'>
                            {[...wcPhoto].map((img, index) => (
                                <div className="image-selected" onClick={() => handleDeleteWCImage(index)}>
                                    <div className='layer-delete'>
                                        <div className='check-delete-image' />
                                        <BsTrash className='image-trash' />
                                    </div>
                                    <img className="image-selected" src={URL.createObjectURL(img)} />
                                </div>
                            ))}
                            <label htmlFor='wcImage' className='add-image-button'>
                                <div className="select-image-service" >
                                    <TbCameraPlus className='icon-camera-plus' />
                                    <div className='text-camera-plus d-block'>{languageList.txtAddImage}</div>
                                </div>
                                <input onChange={(e) => handleWCPhoto(e.target.files)} type="file" id="wcImage" accept="image/*" className='select-image-hide' multiple />
                            </label>
                        </div>
                    </div>
                    <div className='mt-30'>
                        <label className='d-block mb-20'>{languageList.txtOtherPhoto}</label>
                        <div className='grid-image'>
                            {[...otherPhoto].map((img, index) => (
                                <div className="image-selected">
                                    <div className='layer-delete' onClick={() => handleDeleteOtherImage(index)}>
                                        <div className='check-delete-image' />
                                        <BsTrash className='image-trash' />
                                    </div>
                                    <img className="image-selected" src={URL.createObjectURL(img)}/>
                                </div>
                            ))}
                            <label htmlFor='otherImage' className='add-image-button'>
                                <div className="select-image-service" >
                                    <TbCameraPlus className='icon-camera-plus' />
                                    <div className='text-camera-plus d-block'>{languageList.txtAddImage}</div>
                                </div>
                                <input onChange={(e) => handleOtherPhoto(e.target.files)} type="file" id="otherImage" accept="image/*" className='select-image-hide' multiple />
                            </label>
                        </div>
                    </div>
                </div>
            </div>
            <button onClick={handleClickSaveAndNext} className='btn btn-primary btn-submit'>{`${languageList.btnSave} `} <BsArrowRight /></button>
        </div>
    )
}

export default memo(RegisterInformationRooms)
