        slidesToSlide: 1 // optional, default to 1.
    }
};

function TourDetail({ languageSelected }) {
    const tagList = (languageSelected === 'EN' ? english : vietnamese)

    const languageList = (languageSelected === 'EN' ? englishTourDetail : vietnameseTourDetail)

    const tour = {
        images: [BackgroundHome, Hotel1, Hotel2, Hotel3, Hotel4, Attraction1, Attraction2],
        name: 'Tour Tam Chúc và Tràng An - 1 ngày',
        tag: [1, 2, 3],
        introduce: '<ul><li class="ql-align-justify">Chạy trốn Hà Nội vội vã và đến với bức tranh thiên nhiên tuyệt đẹp của Tràng An và chốn tâm linh yên bình của Tam Chúc</li><li class="ql-align-justify">Chiêm ngưỡng quần thể chùa Tam Chúc nức tiếng Hà Nam được bao quanh bởi hệ thống sông và những thửa rừng xanh mướt</li><li class="ql-align-justify">Tham quan Quần thể danh thắng Tràng An, di sản văn hóa và thiên nhiên thế giới do UNESCO công nhận</li><li class="ql-align-justify">Dành cho bản thân mình một chút yên tĩnh khi thuyền đưa bạn qua sông để đến những cảnh quan tuyệt vời</li></ul>',
