import { memo, useContext, useState } from 'react'
import { OnShowSidebar } from '../AdminHome'
import { english, vietnamese } from '../../Languages/OptionInputInformationService'
import { RiHotelLine } from 'react-icons/ri'
import './ViewInformationServices.scss'

sessionStorage.setItem('index-service-selected', 1)
function ViewInformationServiceLayout({ languageSelected }) {
    const isShowSidebar = useContext(OnShowSidebar)
    let menuLanguage = (languageSelected === 'EN' ? english : vietnamese)
    const categoryService = sessionStorage.getItem('index-service-selected') - 1
    const [optionMenuService, setOptionMenuService] = useState(0)

    return (
        <div>
            <div className={`menu-view-service-fixed ${isShowSidebar && 'menu-hidden'}`}>
                {menuLanguage[categoryService].map((option, index) => (
                    <div onClick={() => setOptionMenuService(index)}
                        className={`option-menu-fixed ${optionMenuService === index && 'selected-menu-service'} ${isShowSidebar && 'icon-hide-menu-service'}`}>
                        {!isShowSidebar ? option : '...'}
                    </div>
                ))}
            </div>
            <div>

            </div>
        </div>
    )
}

export default memo(ViewInformationServiceLayout)
