import { memo, useState } from 'react'
import { Rating } from 'react-simple-star-rating'
import { BsArrowRight } from 'react-icons/bs'
import Select from 'react-select';
import City from '../../Data/city.json'
import './RegisterInformationAccomodation.scss'

function RegisterInformationAccomodation({ languageSelected, setSelectedOption, handleClickSaveAndNext, propertyName, setPropertyName,
    numberOfFloors, setNumberOfFloors, status, setStatus, starRating, setStarRating, city, setCity, address, setAddress, description, setDescription,
    nameContact, setNameContact, phoneNumberContact, setPhoneNumberContact, emailContact, setEmailContact }) {
    const handleBlur = (input) => {
        input.style.border = 'solid 1px #D9D9D9'
    }

    const handleFocus = (input) => {
        input.style.border = 'solid 1px #4874E8'
    }

    const optionsStatus = [
        { value: 1, label: 'Active' },
        { value: 2, label: 'Close' },
        { value: 3, label: 'Pause' }
    ]

    const handleRating = (rate) => {
        setStarRating(rate)
    }

    return (
        <div className='container'>
            <div className='space-30 background-w'>
                <div className="d-flex line-input line-input-3-line">
                    <div className="w-45">
                        <label htmlFor='roomName' className='d-block'>Property Name<span className="requird-star">*</span></label>
                        <input id='roomName'
                            className='input-inline'
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            onChange={(e) => setPropertyName(e.target.value)}
                            value={propertyName} />
                    </div>
                    <div className='w-45'>
                        <label htmlFor="status" className="d-block">Status<span className="requird-star">*</span></label>
                        <Select className='input-inline select-none-border'
                            onChange={(e) => setStatus(e.value)} isSearchable={false}
                            options={optionsStatus}
                            defaultValue={status === 0 ? optionsStatus[0] : optionsStatus[status - 1]} />
                    </div>
                </div>
                <div className="d-flex line-input line-input-3-line">
                    <div className="w-45">
                        <label htmlFor='numberOfFloors' className='d-block'>Number of floors</label>
                        <input id='numberOfFloors' className='input-inline'
                            value={numberOfFloors}
                            onChange={(e) => setNumberOfFloors(e.target.value)}
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)} />
                    </div>
                    <div className='w-45'>
                        <label className="d-block">Star rating<span className="requird-star">*</span></label>
                        <Rating className="input-inline star-rating" onClick={handleRating} initialValue={starRating} />
                    </div>
                </div>
                <div className="d-flex line-input line-input-3-line">
                    <div className='w-45'>
                        <label htmlFor="city" className="d-block">City<span className="requird-star">*</span></label>
                        <select id='city' className='input-inline had-input'
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            onChange={(e) => setCity(e.target.value)}>
                            {City.map((cityData) => (
                                <option selected={city == cityData.name} value={cityData.name}>{cityData.name}</option>
                            ))}
                        </select>
                    </div>
                    <div className='w-45'>
                        <label htmlFor="address" className="d-block">Address<span className="requird-star">*</span></label>
                        <input value={address} onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            onChange={(e) => setAddress(e.target.value)}
                            id='address' className="input-inline" type='text' />
                    </div>
                </div>
                <div className="line-input">
                    <div className="input-alone">
                        <label htmlFor='description' className='d-block'>Description</label>
                        <textarea rows="4" id='description' className='input-inline'
                            onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)} />
                    </div>
                </div>
            </div>
            <div className='mt-30 space-30 background-w'>
                <label className='title'>Main Contact</label>
                <div className="line-input">
                    <div className="input-alone">
                        <label htmlFor='name' className='d-block'>Name</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            id='name' className="input-inline" type='text' />
                    </div>
                </div>
                <div className="line-input">
                    <div className="input-alone">
                        <label htmlFor='phone' className='d-block'>Phone number</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            id='phone' className="input-inline" type='text' />
                    </div>
                </div>
                <div className="line-input">
                    <div className="input-alone">
                        <label htmlFor='email' className='d-block'>Email</label>
                        <input onFocus={(e) => handleFocus(e.target)}
                            onBlur={(e) => handleBlur(e.target)}
                            id='email' className="input-inline" type='text' />
                    </div>
                </div>
            </div>
            <button onClick={handleClickSaveAndNext} className='btn btn-primary btn-submit'>{`Save and Next Section `} <BsArrowRight /></button>
        </div>
    )

}

export default RegisterInformationAccomodation
