import { memo } from 'react'
import { english, vietnamese } from '../../Languages/Photo'
import { TbCameraPlus } from 'react-icons/tb'
import { BsTrash } from 'react-icons/bs'
import './RegisterPhotos.scss'

function RegisterPhotos({ receptionHallPhoto, setReceptionHallPhoto, outdoorPhoto, setOutdoorPhoto, otherPhotoAccomodation,
    setOtherPhotoAccomodation, languageSelected }) {
    const handleReceptionPhoto = (data) => {
        let listImage = [...receptionHallPhoto]
        for (let i = 0; i < data.length; i++) {
            listImage.push(data[i])
        }
        setReceptionHallPhoto(listImage)
    }

    const handleOutdoorPhoto = (data) => {
        let listImage = [...outdoorPhoto]
        for (let i = 0; i < data.length; i++) {
            listImage.push(data[i])
        }
        setOutdoorPhoto(listImage)
    }

    const handleOtherPhoto = (data) => {
        let listImage = [...otherPhotoAccomodation]
        for (let i = 0; i < data.length; i++) {
            listImage.push(data[i])
        }
        setOtherPhotoAccomodation(listImage)
    }

    const handleDeleteReceptionHallPhoto = (index) => {
        let afterRemove = []
        for (let i = 0; i < receptionHallPhoto.length; i++) {
            if (i != index) {
                afterRemove.push(receptionHallPhoto[i])
            }
        }
        setReceptionHallPhoto(afterRemove)
    }

    const handleDeleteOutdoorPhoto = (index) => {
        let afterRemove = []
        for (let i = 0; i < outdoorPhoto.length; i++) {
            if (i != index) {
                afterRemove.push(outdoorPhoto[i])
            }
        }
        setOutdoorPhoto(afterRemove)
    }

    const handleDeleteOtherImage = (index) => {
        let afterRemove = []
        for (let i = 0; i < otherPhotoAccomodation.length; i++) {
            if (i != index) {
                afterRemove.push(otherPhotoAccomodation[i])
            }
        }
        setOtherPhotoAccomodation(afterRemove)
    }


    return (
        <div className='container'>
            <div className='space-30 background-w'>
                <label className='title'>Image</label>
                <div className='container'>
                    <div>
                        <label className='d-block mb-20'>Reception hall</label>
                        <div className='grid-image'>
                            {[...receptionHallPhoto].map((img, index) => (
                                <div className="image-selected" id={`${index}`}>
                                    <div className='layer-delete' onClick={() => handleDeleteReceptionHallPhoto(index)}>
                                        <div className='check-delete-image' />
                                        <BsTrash className='image-trash' />
                                    </div>
                                    <img className="image-selected" src={URL.createObjectURL(img)} />
                                </div>
                            ))}
                            <label htmlFor='bedRoomImage' className='add-image-button'>
                                <div className="select-image-service" >
                                    <TbCameraPlus className='icon-camera-plus' />
                                    <div className='text-camera-plus d-block'>Add Image</div>
                                </div>
                                <input type="file" id="bedRoomImage" accept="image/*" className='select-image-hide' multiple />
                            </label>
                        </div>
                    </div>
                    <div className='mt-30'>
                        <label className='d-block mb-20'>Outdoor photo</label>
                        <div className='grid-image'>
                            {[...outdoorPhoto].map((img, index) => (
                                <div className="image-selected">
                                    <div className='layer-delete' onClick={() => handleDeleteOutdoorPhoto(index)}>
                                        <div className='check-delete-image' />
                                        <BsTrash className='image-trash' />
                                    </div>
                                    <img className="image-selected" src={URL.createObjectURL(img)} />
                                </div>
                            ))}
                            <label htmlFor='wcImage' className='add-image-button'>
                                <div className="select-image-service" >
                                    <TbCameraPlus className='icon-camera-plus' />
                                    <div className='text-camera-plus d-block'>Add Image</div>
                                </div>
                                <input type="file" id="wcImage" accept="image/*" className='select-image-hide' multiple />
                            </label>
                        </div>
                    </div>
                    <div className='mt-30'>
                        <label className='d-block mb-20'>Other photo</label>
                        <div className='grid-image'>
                            {[...otherPhotoAccomodation].map((img, index) => (
                                <div className="image-selected">
                                    <div className='layer-delete' onClick={() => handleDeleteOtherImage(index)}>
                                        <div className='check-delete-image' />
                                        <BsTrash className='image-trash' />
                                    </div>
                                    <img className="image-selected" src={URL.createObjectURL(img)} />
                                </div>
                            ))}
                            <label htmlFor='otherImage' className='add-image-button'>
                                <div className="select-image-service" >
                                    <TbCameraPlus className='icon-camera-plus' />
                                    <div className='text-camera-plus d-block'>Add Image</div>
                                </div>
                                <input type="file" id="otherImage" accept="image/*" className='select-image-hide' multiple />
                            </label>
                        </div>
                    </div>
                </div>
            </div>
            <button className='btn btn-primary btn-submit btn-submit-photos'>Submit</button>
        </div>
    )
}

export default memo(RegisterPhotos)
