import Header from "../Header";
import Footer from "../Footer";
import { StickyContainer, Sticky } from 'react-sticky';
import './DefaultLayout.css'

function DefaultLayout({ languageSelected, setLanguageSelected, children, setProgress }) {
    return (

        <div className="layout">
            <StickyContainer>
                <Sticky>
                    {({ style }) => (
                        <Header style={style} setLanguageSelected={setLanguageSelected} languageSelected={languageSelected} setProgress={setProgress} />
                    )}
                    <div className="content">
                        {children}
                    </div>
                </Sticky>
            </StickyContainer>
            <Footer languageSelected={languageSelected} setProgress={setProgress} />
        </div>

    )
}

export default DefaultLayout;
