import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';
import { memo, useState, useCallback } from 'react'

function GoogleMapLayout({ style, tour, setTour, hadOnclick }) {
    const center = {
        lat: tour.latitude,
        lng: tour.longitude
    };

    const { isLoaded } = useJsApiLoader({
        id: 'google-map-script',
        googleMapsApiKey: "AIzaSyBZDeLRK0328QFPaSs-OCk03bMUOHXaMys"
    })

    const handleClickMap = ((latLng) => {
        setTour({ ...tour, latitude: latLng.lat, longitude: latLng.lng })
    })

    const [map, setMap] = useState(null)

    const onLoad = useCallback(function callback(map) {
        const bounds = new window.google.maps.LatLngBounds(center);
        map.fitBounds(bounds);
        setMap(map)
    }, [])

    const onUnmount = useCallback(function callback(map) {
        setMap(null)
    }, [])


    return (isLoaded &&
        <GoogleMap
            mapContainerStyle={style}
            zoom={10}
            onLoad={onLoad}
            onUnmount={onUnmount}
            onClick={hadOnclick && ((e) => handleClickMap(e.latLng))}
        >
            <Marker position={{
                lat: tour.latitude,
                lng: tour.longitude
            }} />
        </GoogleMap>
    );
}

export default memo(GoogleMapLayout)
